import{_ as t,c as i,a0 as r,j as n,a,o}from"./chunks/framework.D84hx5uF.js";const m=JSON.parse('{"title":"Sửa đề minh hoạ 2025 môn Tin học","description":"","frontmatter":{"head":[["link",{"rel":"stylesheet","href":"https://cdnjs.cloudflare.com/ajax/libs/KaTeX/0.5.1/katex.min.css"}]]},"headers":[],"relativePath":"posts/Other/suade.md","filePath":"posts/Other/suade.md"}'),u={name:"posts/Other/suade.md"};function l(c,h,e,s,g,p){return o(),i("div",null,h[0]||(h[0]=[r('<h1 id="sua-đe-minh-hoa-2025-mon-tin-hoc" tabindex="-1">Sửa đề minh hoạ 2025 môn Tin học <a class="header-anchor" href="#sua-đe-minh-hoa-2025-mon-tin-hoc" aria-label="Permalink to &quot;Sửa đề minh hoạ 2025 môn Tin học&quot;">​</a></h1><h2 id="gioi-thieu" tabindex="-1">Giới thiệu <a class="header-anchor" href="#gioi-thieu" aria-label="Permalink to &quot;Giới thiệu&quot;">​</a></h2><p>Hôm 29/12 vừa rồi, Bộ Giáo dục và Đào tạo đã công bố cấu trúc đề thi tốt nghiệp THPT năm 2025 cho các môn dành cho học sinh 2007, là lứa đầu tiên tốt nghiệp THPT theo chương trình 2018.</p><p>Một điểm mới của kỳ thi 2025 là bổ sung thêm các môn như Công nghệ và Tin học, vốn không phải là môn thi tốt nghiệp trong quá khứ, và thường bị xem là &quot;môn phụ&quot;. Hy vọng rằng kể từ 2025, việc thêm môn thi này sẽ thúc đẩy việc dạy &amp; học Tin học trong trường phổ thông, vốn là môn học vô cùng quan trọng trong thời kỳ 4.0.</p><p>Trong post này, mình sẽ sửa đề minh hoạ môn Tin học, mặc dù nó rất dễ và hầu như ai cũng có thể làm được. Tuy nhiên mình vẫn viết vì <s>mình thích và để chống blog chết</s> mình muốn tất cả những bạn không học Tin học hiểu sâu hơn một chút.</p><h2 id="đe-thi" tabindex="-1">Đề thi <a class="header-anchor" href="#đe-thi" aria-label="Permalink to &quot;Đề thi&quot;">​</a></h2><p>Mọi người có thể xem ở đây: <a href="https://cdn.tuoitre.vn/471584752817336320/2023/12/29/9-tin-hoc-17038250300841507979492.pdf" target="_blank" rel="noreferrer">Đề thi</a></p><p>Có thể thấy đề thi gồm các phần:</p><ul><li>Trắc nghiệm nhiều phương án lựa chọn</li><li>Trắc nghiệm đúng/sai</li></ul><p>Ở trong phần trắc nghiệm đúng/sai, có phần chung &amp; phần chia ra 2 định hướng là Khoa học máy tính và Tin học ứng dụng, học sinh chọn một trong hai phần để làm. Trong post này, mình chỉ sửa ngang tới phần Khoa học máy tính, còn phần Tin học ứng dụng thì mình sẽ không sửa, vì mình không rành thiết kế đồ hoạ trên máy tính.</p><h2 id="sua-đe-thi" tabindex="-1">Sửa đề thi <a class="header-anchor" href="#sua-đe-thi" aria-label="Permalink to &quot;Sửa đề thi&quot;">​</a></h2><p>Lưu ý: Những câu nào mình thấy dễ quá thì mình chỉ đưa ra đáp án, không giải thích gì thêm. Tuy nhiên nếu bạn cảm thấy khó hiểu ở đâu thì hãy nhắn tin cho mình qua discord, mình sẽ bổ sung phần giải thích cho các bạn.</p><h3 id="phan-i-trac-nghiem-nhieu-phuong-an-lua-chon" tabindex="-1">Phần I. Trắc nghiệm nhiều phương án lựa chọn <a class="header-anchor" href="#phan-i-trac-nghiem-nhieu-phuong-an-lua-chon" aria-label="Permalink to &quot;Phần I. Trắc nghiệm nhiều phương án lựa chọn&quot;">​</a></h3><h4 id="cau-1" tabindex="-1">Câu 1 <a class="header-anchor" href="#cau-1" aria-label="Permalink to &quot;Câu 1&quot;">​</a></h4><p>Vậy đáp án là <strong>D</strong>.</p><h4 id="cau-2" tabindex="-1">Câu 2 <a class="header-anchor" href="#cau-2" aria-label="Permalink to &quot;Câu 2&quot;">​</a></h4><ul><li>Câu A: <strong>Sai</strong>, $1$GB = $1024$ KB</li><li>Câu B: <strong>Sai</strong>, $1$MB = $1024$ KB</li><li>Câu C: <strong>Đúng</strong>, theo thứ tự: B &lt; KB &lt; MB &lt; GB &lt; TB &lt; ...</li><li>Câu D: <strong>Sai</strong>, $1$B = 8 bit.</li></ul><p>Vậy đáp án là <strong>C</strong></p><h4 id="cau-3" tabindex="-1">Câu 3 <a class="header-anchor" href="#cau-3" aria-label="Permalink to &quot;Câu 3&quot;">​</a></h4><ul><li>Câu A: <strong>Sai</strong>, =))))</li><li>Câu B: <strong>Đúng</strong>, tuy nhiên mình thấy câu này hơi cấn cấn</li><li>Câu C: <strong>Đúng</strong>, làm việc được liên tục 24/24 không nghỉ</li><li>Câu D: <strong>Đúng</strong>, khi cung cấp thao tác và chương trình thì máy tính mới làm việc được</li></ul><p>Vậy đáp án là <strong>A</strong></p><h4 id="cau-4" tabindex="-1">Câu 4 <a class="header-anchor" href="#cau-4" aria-label="Permalink to &quot;Câu 4&quot;">​</a></h4><ul><li>Câu A: <strong>Sai</strong>, nó sẽ tự đi quanh nhà và hút sạch những chỗ có bụi</li><li>Câu B: <strong>Đúng</strong>, máy tính đã được phát minh cách đây rất lâu, chỉ dùng để tính toán nên không thông minh</li><li>Câu C: <strong>Sai</strong>, nó sử dụng thuật toán để nhận diện kẻ đột nhập</li><li>Câu D: <strong>Sai</strong>, nó sử dụng hệ thống &amp; thuật toán để chạy trên đường</li></ul><p>Vậy đáp án là <strong>B</strong></p><h4 id="cau-5" tabindex="-1">Câu 5 <a class="header-anchor" href="#cau-5" aria-label="Permalink to &quot;Câu 5&quot;">​</a></h4><ul><li>Câu A, B, D: Đều phải tải về mới xài được</li><li>Câu C: khi bạn mua máy điện thoại thông minh mới về thì lúc nào cũng có trình duyệt trong máy</li></ul><p>Vậy đáp án là <strong>C</strong></p><h4 id="cau-6" tabindex="-1">Câu 6 <a class="header-anchor" href="#cau-6" aria-label="Permalink to &quot;Câu 6&quot;">​</a></h4><ul><li>Câu B, C, D: Sao phải phức tạp thế =)))</li><li>Câu A: Làm như này cho nhanh phải không =))))))</li></ul><p>Vậy đáp án là <strong>A</strong></p><h4 id="cau-7" tabindex="-1">Câu 7 <a class="header-anchor" href="#cau-7" aria-label="Permalink to &quot;Câu 7&quot;">​</a></h4><p><img src="https://i.imgur.com/84Dgoj1.png" alt="Imgur"></p><p>Câu <strong>D</strong> là đáp án đúng.</p><h4 id="cau-8" tabindex="-1">Câu 8 <a class="header-anchor" href="#cau-8" aria-label="Permalink to &quot;Câu 8&quot;">​</a></h4><p><img src="https://i.imgur.com/7u285wV.png" alt="Imgur"></p><ul><li>Câu B, C: Có thể sử dụng cơ sở dữ liệu hoặc một trình quản lý nào đó để thực hiện, không nhất thiết phải sử dụng IoT</li><li>Câu D: dùng phần mềm quét thông thường</li><li>Câu A: sự kết hợp giữa nhiều hệ thống khác nhau: cảm biến xe, hệ thống thu phí, quản lý phí, báo cáo, blabla....</li></ul><p>Vậy đáp án là <strong>A</strong></p><h4 id="cau-9" tabindex="-1">Câu 9 <a class="header-anchor" href="#cau-9" aria-label="Permalink to &quot;Câu 9&quot;">​</a></h4><p>Đáp án đúng là câu <strong>B</strong></p><h4 id="cau-10" tabindex="-1">Câu 10 <a class="header-anchor" href="#cau-10" aria-label="Permalink to &quot;Câu 10&quot;">​</a></h4><p>Đáp án đúng là câu <strong>B</strong></p><h4 id="cau-11" tabindex="-1">Câu 11 <a class="header-anchor" href="#cau-11" aria-label="Permalink to &quot;Câu 11&quot;">​</a></h4><p>Đáp án đúng là câu <strong>D</strong></p><h4 id="cau-12" tabindex="-1">Câu 12 <a class="header-anchor" href="#cau-12" aria-label="Permalink to &quot;Câu 12&quot;">​</a></h4><p>Đáp án đúng là câu <strong>C</strong></p><h4 id="cau-13" tabindex="-1">Câu 13 <a class="header-anchor" href="#cau-13" aria-label="Permalink to &quot;Câu 13&quot;">​</a></h4><p>Đáp án đúng là câu <strong>A</strong></p><h4 id="cau-14" tabindex="-1">Câu 14 <a class="header-anchor" href="#cau-14" aria-label="Permalink to &quot;Câu 14&quot;">​</a></h4><p>Đáp án đúng là câu <strong>C</strong></p><h4 id="cau-15" tabindex="-1">Câu 15 <a class="header-anchor" href="#cau-15" aria-label="Permalink to &quot;Câu 15&quot;">​</a></h4><ul><li>Câu A, C, D đều là phép <strong>so sánh</strong>, câu B là <strong>phép gán</strong></li></ul><p>Đáp án đúng là câu <strong>B</strong></p><h4 id="cau-16" tabindex="-1">Câu 16 <a class="header-anchor" href="#cau-16" aria-label="Permalink to &quot;Câu 16&quot;">​</a></h4><p>$5/2=2.5$</p><p>Đáp án đúng là câu <strong>C</strong></p><h4 id="cau-17" tabindex="-1">Câu 17 <a class="header-anchor" href="#cau-17" aria-label="Permalink to &quot;Câu 17&quot;">​</a></h4><p>Nhập cho tới khi nào thoả một điều kiện nào đó, đặc trưng của câu lệnh <code>while</code> là một <strong>vòng lặp</strong></p><p>Đáp án đúng là câu <strong>A</strong></p><h4 id="cau-18" tabindex="-1">Câu 18 <a class="header-anchor" href="#cau-18" aria-label="Permalink to &quot;Câu 18&quot;">​</a></h4><p>Viết chương trình con <strong>KHÔNG</strong> làm cho chương trình chạy nhanh hơn, chỉ gọn hơn và nhìn khoa học hơn thôi.</p><p>Đáp án đúng là câu <strong>A</strong></p><h4 id="cau-19" tabindex="-1">Câu 19 <a class="header-anchor" href="#cau-19" aria-label="Permalink to &quot;Câu 19&quot;">​</a></h4><p>Nhất định phải có tên hàm thì lúc cần mới gọi hàm được.</p><p>Đáp án đúng là câu <strong>D</strong></p><h4 id="cau-20" tabindex="-1">Câu 20 <a class="header-anchor" href="#cau-20" aria-label="Permalink to &quot;Câu 20&quot;">​</a></h4><p>Hàm <code>tinh</code> sẽ in ra giá trị $b=a^a$. Thay $a=3$ thì $b=3^3=27$</p><p>Đáp án đúng là câu <strong>B</strong></p><h4 id="cau-21" tabindex="-1">Câu 21 <a class="header-anchor" href="#cau-21" aria-label="Permalink to &quot;Câu 21&quot;">​</a></h4><p>Đáp án đúng là câu <strong>A</strong>, cú pháp cơ bản</p><h4 id="cau-22" tabindex="-1">Câu 22 <a class="header-anchor" href="#cau-22" aria-label="Permalink to &quot;Câu 22&quot;">​</a></h4><p>Dòng $3$, sau <code>if</code> phải thụt lề</p><p>Đáp án đúng là câu <strong>D</strong></p><h4 id="cau-23" tabindex="-1">Câu 23 <a class="header-anchor" href="#cau-23" aria-label="Permalink to &quot;Câu 23&quot;">​</a></h4><p>Đáp án đúng là câu <strong>A</strong>, cú pháp cơ bản</p><h4 id="cau-24" tabindex="-1">Câu 24 <a class="header-anchor" href="#cau-24" aria-label="Permalink to &quot;Câu 24&quot;">​</a></h4><p>Đáp án đúng là câu <strong>A</strong></p><h3 id="phan-2-trac-nghiem-đung-sai" tabindex="-1">Phần 2. Trắc nghiệm đúng/sai <a class="header-anchor" href="#phan-2-trac-nghiem-đung-sai" aria-label="Permalink to &quot;Phần 2. Trắc nghiệm đúng/sai&quot;">​</a></h3><h4 id="phan-a-danh-cho-moi-thi-sinh" tabindex="-1">Phần A. Dành cho mọi thí sinh <a class="header-anchor" href="#phan-a-danh-cho-moi-thi-sinh" aria-label="Permalink to &quot;Phần A. Dành cho mọi thí sinh&quot;">​</a></h4><h5 id="cau-1-1" tabindex="-1">Câu 1. <a class="header-anchor" href="#cau-1-1" aria-label="Permalink to &quot;Câu 1.&quot;">​</a></h5><ul><li>Câu a <strong>SAI</strong>, vì <code>float</code> là kiểu số thực</li><li>Câu b <strong>ĐÚNG</strong>, cú pháp cơ bản</li><li>Câu c <strong>SAI</strong>, $1^2+2^2=5$</li><li>Câu d <strong>ĐÚNG</strong>, $cheo=\\sqrt{K}$, dùng căn bậc hai để tính</li></ul><h5 id="cau-2-1" tabindex="-1">Câu 2. <a class="header-anchor" href="#cau-2-1" aria-label="Permalink to &quot;Câu 2.&quot;">​</a></h5><ul><li>Câu a <strong>SAI</strong>, chương trình tìm số lượng số chẵn (có điều kiện <code>L%2==0</code>)</li><li>Câu b <strong>ĐÚNG</strong>, trong đoạn $[3,7]$ có $2$ số chẵn là $4$ và $6$</li><li>Câu c <strong>ĐÚNG</strong>, vì để dừng chương trình thì $L$ phải bằng $7$ (theo điều kiện)</li><li>Câu d <strong>SAI</strong>, có thể nhập $R=8$ vẫn cho ra kết quả y hệt.</li></ul><h4 id="phan-b-phan-khoa-hoc-may-tinh" tabindex="-1">Phần B. Phần khoa học máy tính <a class="header-anchor" href="#phan-b-phan-khoa-hoc-may-tinh" aria-label="Permalink to &quot;Phần B. Phần khoa học máy tính&quot;">​</a></h4><h5 id="cau-3-1" tabindex="-1">Câu 3. <a class="header-anchor" href="#cau-3-1" aria-label="Permalink to &quot;Câu 3.&quot;">​</a></h5>',84),n("ul",null,[n("li",null,[a("Câu a "),n("strong",null,"SAI"),a(", $(1001)"),n("em",{10:""},"2=(9)"),a("$")]),n("li",null,[a("Câu b "),n("strong",null,"ĐÚNG"),a(", cụ thể khi giảm còn $(1000)_2$ thì giá trị thập phân của nó bằng $8$")]),n("li",null,[a("Câu c "),n("strong",null,"ĐÚNG"),a(":")])],-1),n("p",null,[n("img",{src:"https://i.imgur.com/P8UY1Id.png",alt:"Imgur"}),a(" Tính từ trái sang phải: 1 + 1 = 10, viết 0 nhớ 1 0 + 1 = 1, thêm 1 bằng 10, viết 0 nhớ 1 0 + 1(nhớ) = 1 Hạ 1 xuống")],-1),n("ul",null,[n("li",null,[a("Câu d "),n("strong",null,"ĐÚNG"),a(":")])],-1),n("p",null,"$1001\\ AND\\ 11 = 1001$",-1),n("p",null,"$(1001\\ AND\\ 11)\\ OR\\ 1001 = 1001\\ OR\\ 1001 = 1001$",-1)]))}const C=t(u,[["render",l]]);export{m as __pageData,C as default};
